import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import html2pdf from 'html2pdf.js/dist/html2pdf.bundle.min';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [file, setFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState('checking');
  const [messages, setMessages] = useState([
    {
      type: 'assistant',
      content: '你好！我是財報分析助手。請上傳一份財報 PDF，我會為你進行詳細分析。'
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    fetch(API_BASE_URL)
      .then(response => response.json())
      .then(data => {
        if (data.status === 'ok') {
          setApiStatus('ready');
        } else {
          setApiStatus('error');
          setError('API 服務異常');
        }
      })
      .catch(error => {
        setApiStatus('error');
        setError('無法連接到 API 服務');
      });
  }, []);

  const handleFileUpload = async (event) => {
    const uploadedFile = event.target.files[0];
    if (!uploadedFile) return;

    if (uploadedFile.type !== 'application/pdf') {
      setError('請上傳 PDF 檔案');
      setMessages(prev => [...prev, 
        { type: 'user', content: `上傳檔案：${uploadedFile.name}` },
        { type: 'assistant', content: '抱歉，請上傳 PDF 格式的檔案。' }
      ]);
      return;
    }

    setFile(uploadedFile);
    setError(null);
    setLoading(true);
    setMessages(prev => [...prev, 
      { type: 'user', content: `上傳檔案：${uploadedFile.name}` },
      { type: 'assistant', content: '好的，我正在分析這份財報，請稍候...' }
    ]);

    const formData = new FormData();
    formData.append('file', uploadedFile);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || '分析過程發生錯誤');
      }

      setAnalysis(data);
      setMessages(prev => [...prev, 
        { type: 'assistant', content: '分析完成！以下是主要發現：\n\n' + data.summary }
      ]);
    } catch (error) {
      setError(error.message);
      setMessages(prev => [...prev, 
        { type: 'assistant', content: `分析時發生錯誤：${error.message}` }
      ]);
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    setMessages(prev => [...prev, { type: 'user', content: inputMessage }]);
    setInputMessage('');

    // 這裡可以根據用戶的輸入來提供相應的回應
    if (analysis) {
      let response = '';
      if (inputMessage.includes('獲利') || inputMessage.includes('盈利')) {
        response = analysis.analysis.profitability;
      } else if (inputMessage.includes('財務') || inputMessage.includes('結構')) {
        response = analysis.analysis.financial_structure;
      } else if (inputMessage.includes('營運') || inputMessage.includes('效率')) {
        response = analysis.analysis.operational_efficiency;
      } else if (inputMessage.includes('風險')) {
        response = analysis.analysis.risk_assessment;
      } else if (inputMessage.includes('建議') || inputMessage.includes('投資')) {
        response = analysis.analysis.investment_advice;
      } else {
        response = '請問您想了解哪個方面的分析？可以詢問：獲利能力、財務結構、營運效率、風險評估或投資建議。';
      }
      setMessages(prev => [...prev, { type: 'assistant', content: response }]);
    } else {
      setMessages(prev => [...prev, 
        { type: 'assistant', content: '請先上傳財報 PDF，我才能為您提供分析。' }
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatNumber = (value) => {
    if (typeof value !== 'number') return value;
    if (Math.abs(value) >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(2)} 十億`;
    } else if (Math.abs(value) >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(2)} 百萬`;
    } else if (Math.abs(value) >= 1_000) {
      return `${(value / 1_000).toFixed(2)} 千`;
    }
    return value.toFixed(2);
  };

  const formatPercentage = (value) => {
    if (typeof value !== 'number') return value;
    return `${(value * 100).toFixed(2)}%`;
  };

  if (apiStatus === 'checking') {
    return (
      <div className="App">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>正在連接到 API 服務...</p>
        </div>
      </div>
    );
  }

  if (apiStatus === 'error') {
    return (
      <div className="App">
        <div className="error-message">
          <h2>服務連接錯誤</h2>
          <p>{error}</p>
          <p>請確認後端服務是否正常運行</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>財報分析 GPT</h1>
        <div className="upload-container">
          <label className="upload-button" htmlFor="file-upload">
            {file ? '重新選擇檔案' : '選擇財報 PDF'}
            <input
              id="file-upload"
              type="file"
              accept=".pdf"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </label>
          {file && <p className="file-name">已選擇：{file.name}</p>}
        </div>
      </header>

      <main className="App-main">
        <div className="chat-container">
          <div className="chat-messages">
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.type}`}>
                <div className={`avatar ${message.type}`}>
                  {message.type === 'assistant' ? '🤖' : '👤'}
                </div>
                <div className="message-content">
                  {message.content}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          <div className="chat-input">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="輸入您的問題..."
              disabled={loading || !apiStatus === 'ready'}
            />
            <button 
              onClick={handleSendMessage}
              disabled={loading || !apiStatus === 'ready'}
            >
              發送
            </button>
          </div>
        </div>

        {analysis && (
          <div className="download-button-container">
            <button onClick={() => {
              const content = document.getElementById('report-content');
              const opt = {
                margin: 1,
                filename: '財報分析報告.pdf',
                image: { type: 'jpeg', quality: 0.98 },
                html2canvas: { scale: 2 },
                jsPDF: { unit: 'cm', format: 'a4', orientation: 'portrait' }
              };
              html2pdf().set(opt).from(content).save();
            }} className="download-button">
              下載完整報告
            </button>
          </div>
        )}

        {/* 隱藏的報告內容，用於生成 PDF */}
        {analysis && (
          <div id="report-content" className="report-content" style={{ display: 'none' }}>
            <div className="report-header">
              <h2>財務分析報告</h2>
              <p className="report-date">報告日期：{new Date().toLocaleDateString('zh-TW')}</p>
            </div>

            <section className="report-section">
              <h3>財務摘要</h3>
              <p>{analysis.summary}</p>
            </section>

            <section className="report-section">
              <h3>獲利能力分析</h3>
              <p>{analysis.analysis.profitability}</p>
            </section>

            <section className="report-section">
              <h3>財務結構分析</h3>
              <p>{analysis.analysis.financial_structure}</p>
            </section>

            <section className="report-section">
              <h3>營運效率分析</h3>
              <p>{analysis.analysis.operational_efficiency}</p>
            </section>

            <section className="report-section">
              <h3>風險評估</h3>
              <p>{analysis.analysis.risk_assessment}</p>
            </section>

            <section className="report-section">
              <h3>投資建議</h3>
              <p>{analysis.analysis.investment_advice}</p>
            </section>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
