import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000';

const ANALYSIS_SECTIONS = {
  profitability: {
    title: 'Áç≤Âà©ËÉΩÂäõÂàÜÊûê',
    icon: 'üìà'
  },
  financial_structure: {
    title: 'Ë≤°ÂãôÁµêÊßãÂàÜÊûê',
    icon: 'üèóÔ∏è'
  },
  operational_efficiency: {
    title: 'ÁáüÈÅãÊïàÁéáÂàÜÊûê',
    icon: '‚öôÔ∏è'
  },
  risk_assessment: {
    title: 'È¢®Èö™Ë©ï‰º∞',
    icon: '‚ö†Ô∏è'
  },
  investment_advice: {
    title: 'ÊäïË≥áÂª∫Ë≠∞',
    icon: 'üí°'
  }
};

function App() {
  const [file, setFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState('checking');
  const [activeSection, setActiveSection] = useState('summary');

  useEffect(() => {
    // Ê™¢Êü• API ÊúçÂãôÊòØÂê¶Ê≠£Â∏∏ÈÅãË°å
    fetch(API_BASE_URL)
      .then(response => response.json())
      .then(data => {
        if (data.status === 'ok') {
          setApiStatus('ready');
        } else {
          setApiStatus('error');
          setError('API ÊúçÂãôÁï∞Â∏∏');
        }
      })
      .catch(error => {
        setApiStatus('error');
        setError('ÁÑ°Ê≥ïÈÄ£Êé•Âà∞ API ÊúçÂãô');
      });
  }, []);

  const handleFileUpload = async (event) => {
    const uploadedFile = event.target.files[0];
    if (!uploadedFile) return;
    
    if (uploadedFile.type !== 'application/pdf') {
      setError('Ë´ã‰∏äÂÇ≥ PDF Ê™îÊ°à');
      return;
    }

    setFile(uploadedFile);
    setError(null);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', uploadedFile);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'ÂàÜÊûêÈÅéÁ®ãÁôºÁîüÈåØË™§');
      }
      
      setAnalysis(data);
      setActiveSection('summary');
    } catch (error) {
      setError(error.message);
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Ê†ºÂºèÂåñÊï∏Â≠ó
  const formatNumber = (value) => {
    if (typeof value !== 'number') return value;
    if (Math.abs(value) >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(2)} ÂçÅÂÑÑ`;
    } else if (Math.abs(value) >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(2)} ÁôæËê¨`;
    } else if (Math.abs(value) >= 1_000) {
      return `${(value / 1_000).toFixed(2)} ÂçÉ`;
    }
    return value.toFixed(2);
  };

  // Ê†ºÂºèÂåñÁôæÂàÜÊØî
  const formatPercentage = (value) => {
    if (typeof value !== 'number') return value;
    return `${(value * 100).toFixed(2)}%`;
  };

  if (apiStatus === 'checking') {
    return (
      <div className="App">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Ê≠£Âú®ÈÄ£Êé•Âà∞ API ÊúçÂãô...</p>
        </div>
      </div>
    );
  }

  if (apiStatus === 'error') {
    return (
      <div className="App">
        <div className="error-message">
          <h2>ÊúçÂãôÈÄ£Êé•ÈåØË™§</h2>
          <p>{error}</p>
          <p>Ë´ãÁ¢∫Ë™çÂæåÁ´ØÊúçÂãôÊòØÂê¶Ê≠£Â∏∏ÈÅãË°å</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Ë≤°Â†±ÂàÜÊûê GPT</h1>
        <div className="upload-container">
          <label className="upload-button" htmlFor="file-upload">
            {file ? 'ÈáçÊñ∞ÈÅ∏ÊìáÊ™îÊ°à' : 'ÈÅ∏ÊìáË≤°Â†± PDF'}
            <input
              id="file-upload"
              type="file"
              accept=".pdf"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </label>
          {file && <p className="file-name">Â∑≤ÈÅ∏ÊìáÔºö{file.name}</p>}
        </div>
      </header>

      <main className="App-main">
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}
        
        {loading && (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Ê≠£Âú®ÂàÜÊûêË≤°Â†±ÔºåË´ãÁ®çÂÄô...</p>
          </div>
        )}

        {analysis && !loading && (
          <div className="analysis-results">
            <div className="analysis-nav">
              <button 
                className={activeSection === 'summary' ? 'active' : ''} 
                onClick={() => setActiveSection('summary')}
              >
                üìä Ë≤°ÂãôÊëòË¶Å
              </button>
              {Object.entries(ANALYSIS_SECTIONS).map(([key, section]) => (
                <button
                  key={key}
                  className={activeSection === key ? 'active' : ''}
                  onClick={() => setActiveSection(key)}
                >
                  {section.icon} {section.title}
                </button>
              ))}
            </div>

            <div className="analysis-content">
              {activeSection === 'summary' ? (
                <>
                  <div className="result-section">
                    <h3>Ë≤°ÂãôÊëòË¶Å</h3>
                    <p>{analysis.summary}</p>
                  </div>
                  <div className="result-section">
                    <h3>ÈáçË¶ÅÊåáÊ®ô</h3>
                    <div className="indicators-grid">
                      {analysis.indicators && Object.entries(analysis.indicators).map(([key, value]) => (
                        <div key={key} className="indicator-item">
                          <span className="indicator-label">{key}Ôºö</span>
                          <span className="indicator-value">
                            {key.includes('Áéá') || key === 'ROA' || key === 'ROE' 
                              ? formatPercentage(value)
                              : formatNumber(value)}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                </>
              ) : (
                <div className="result-section">
                  <h3>{ANALYSIS_SECTIONS[activeSection].icon} {ANALYSIS_SECTIONS[activeSection].title}</h3>
                  <div className="analysis-detail">
                    {analysis.analysis[activeSection]}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
