import React, { useState } from 'react';
import { uploadFile, analyzeReport } from './api';
import './App.css';
import html2pdf from 'html2pdf.js/dist/html2pdf.bundle.min';

function App() {
  const [file, setFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError(null);
  };

  const handleUpload = async () => {
    if (!file) {
      setError('請選擇一個 PDF 文件');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const uploadResponse = await uploadFile(file);
      const analysisResponse = await analyzeReport(uploadResponse.filename);
      setAnalysis(analysisResponse);
      setCurrentPage(1);
    } catch (err) {
      setError('處理文件時發生錯誤');
      console.error('Error:', err);
    }
    setLoading(false);
  };

  const downloadPDF = () => {
    if (!analysis) return;

    const content = document.getElementById('analysis-content');
    const opt = {
      margin: 1,
      filename: '財報分析報告.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'cm', format: 'a4', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(content).save();
  };

  const renderContent = () => {
    if (!analysis) return null;

    const sections = [
      {
        title: '財報分析摘要',
        content: analysis.summary
      },
      {
        title: '獲利能力分析',
        content: analysis.profitability_analysis
      },
      {
        title: '財務結構分析',
        content: analysis.financial_structure_analysis
      },
      {
        title: '營運效率分析',
        content: analysis.operational_efficiency_analysis
      },
      {
        title: '投資建議',
        content: analysis.investment_advice
      }
    ];

    const itemsPerPage = 2;
    const totalPages = Math.ceil(sections.length / itemsPerPage);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const visibleSections = sections.slice(startIndex, startIndex + itemsPerPage);

    return (
      <div className="analysis-container">
        <div className="analysis-header">
          <h1>財報分析報告</h1>
          <div className="action-buttons">
            <button onClick={downloadPDF} className="download-btn">
              下載 PDF
            </button>
          </div>
        </div>

        <div id="analysis-content" className="analysis-content">
          {visibleSections.map((section, index) => (
            <div key={index} className="analysis-section">
              <h2>{section.title}</h2>
              <div className="section-content">
                {section.content.split('\n').map((paragraph, i) => (
                  <p key={i}>{paragraph}</p>
                ))}
              </div>
            </div>
          ))}
        </div>

        {totalPages > 1 && (
          <div className="pagination">
            <button 
              onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
              disabled={currentPage === 1}
              className="page-btn"
            >
              上一頁
            </button>
            <span className="page-info">
              第 {currentPage} 頁，共 {totalPages} 頁
            </span>
            <button 
              onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
              disabled={currentPage === totalPages}
              className="page-btn"
            >
              下一頁
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="app">
      <div className="upload-section">
        <input
          type="file"
          onChange={handleFileChange}
          accept=".pdf"
          className="file-input"
        />
        <button 
          onClick={handleUpload}
          disabled={loading}
          className="upload-btn"
        >
          {loading ? '分析中...' : '上傳並分析'}
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>正在分析財報，請稍候...</p>
        </div>
      ) : (
        renderContent()
      )}
    </div>
  );
}

export default App;
