import React, { useState } from 'react';
import axios from 'axios';
import html2pdf from 'html2pdf.js';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 3; // 每頁顯示的分析部分數量

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError(null);
  };

  const handleAnalyze = async () => {
    if (!file) {
      setError('請先選擇檔案');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/analyze', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnalysisResult(response.data);
      setCurrentPage(1);
    } catch (err) {
      setError('分析過程發生錯誤，請稍後再試');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = () => {
    const element = document.getElementById('analysis-content');
    const opt = {
      margin: 1,
      filename: '財報分析報告.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(element).save();
  };

  // 將分析結果分成不同部分
  const getAnalysisSections = () => {
    if (!analysisResult) return [];

    const sections = [
      {
        title: '獲利能力分析',
        content: analysisResult.profitability_analysis
      },
      {
        title: '財務結構分析',
        content: analysisResult.financial_structure_analysis
      },
      {
        title: '營運效率分析',
        content: analysisResult.operational_efficiency_analysis
      },
      {
        title: '現金流量分析',
        content: analysisResult.cash_flow_analysis
      },
      {
        title: '風險評估',
        content: analysisResult.risk_assessment
      },
      {
        title: '投資建議',
        content: analysisResult.investment_advice
      }
    ].filter(section => section.content); // 只保留有內容的部分

    return sections;
  };

  // 計算總頁數
  const totalPages = Math.ceil(getAnalysisSections().length / itemsPerPage);

  // 獲取當前頁面的內容
  const getCurrentPageContent = () => {
    const sections = getAnalysisSections();
    const startIndex = (currentPage - 1) * itemsPerPage;
    return sections.slice(startIndex, startIndex + itemsPerPage);
  };

  return (
    <div className="App">
      <header className="header">
        <h1>財報分析系統</h1>
      </header>

      <main>
        <section className="upload-section">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="file-input"
          />
          <label htmlFor="file-input" className="upload-button">
            選擇檔案
          </label>
          {file && <span className="file-name">{file.name}</span>}
          <button
            className="analyze-button"
            onClick={handleAnalyze}
            disabled={!file || loading}
          >
            {loading ? '分析中...' : '開始分析'}
          </button>
        </section>

        {error && <div className="error-message">{error}</div>}

        {loading && (
          <div className="loading-overlay">
            <div className="loading-spinner"></div>
          </div>
        )}

        {analysisResult && (
          <div className="result-section">
            <div id="analysis-content">
              {getCurrentPageContent().map((section, index) => (
                <div key={index} className="analysis-section">
                  <h2>{section.title}</h2>
                  <div className="analysis-content">
                    {section.content.split('\n').map((paragraph, i) => (
                      <p key={i}>{paragraph}</p>
                    ))}
                  </div>
                </div>
              ))}
            </div>

            {totalPages > 1 && (
              <div className="page-navigation">
                <button
                  className="page-button"
                  onClick={() => setCurrentPage(prev => prev - 1)}
                  disabled={currentPage === 1}
                >
                  上一頁
                </button>
                <span className="page-indicator">
                  第 {currentPage} 頁，共 {totalPages} 頁
                </span>
                <button
                  className="page-button"
                  onClick={() => setCurrentPage(prev => prev + 1)}
                  disabled={currentPage === totalPages}
                >
                  下一頁
                </button>
              </div>
            )}

            <button className="download-button" onClick={handleDownloadPDF}>
              下載 PDF 報告
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
