import React, { useState, useEffect } from 'react';
import './App.css';
import html2pdf from 'html2pdf.js/dist/html2pdf.bundle.min';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [file, setFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState('checking');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 3; // 每頁顯示的分析項目數

  useEffect(() => {
    fetch(API_BASE_URL)
      .then(response => response.json())
      .then(data => {
        if (data.status === 'ok') {
          setApiStatus('ready');
        } else {
          setApiStatus('error');
          setError('API 服務異常');
        }
      })
      .catch(error => {
        setApiStatus('error');
        setError('無法連接到 API 服務');
      });
  }, []);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/analyze`, formData);
      setAnalysis(response.data);
    } catch (error) {
      console.error('Error:', error);
      alert('分析過程中發生錯誤，請稍後再試。');
    }
    setLoading(false);
  };

  const formatNumber = (value) => {
    if (typeof value !== 'number') return value;
    if (Math.abs(value) >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(2)} 十億`;
    } else if (Math.abs(value) >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(2)} 百萬`;
    } else if (Math.abs(value) >= 1_000) {
      return `${(value / 1_000).toFixed(2)} 千`;
    }
    return value.toFixed(2);
  };

  const formatPercentage = (value) => {
    if (typeof value !== 'number') return value;
    return `${(value * 100).toFixed(2)}%`;
  };

  const handleDownloadPDF = () => {
    const content = document.getElementById('analysis-content');
    const opt = {
      margin: 1,
      filename: '財報分析報告.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(content).save();
  };

  // 將分析內容分頁
  const getPageContent = () => {
    if (!analysis) return [];

    const sections = [
      { title: '獲利能力分析', content: analysis.profitability_analysis },
      { title: '財務結構分析', content: analysis.financial_structure_analysis },
      { title: '營運效率分析', content: analysis.operational_efficiency_analysis },
      { title: '現金流量分析', content: analysis.cash_flow_analysis },
      { title: '風險評估', content: analysis.risk_assessment },
      { title: '投資建議', content: analysis.investment_advice }
    ];

    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return sections.slice(startIndex, endIndex);
  };

  const totalPages = analysis ? Math.ceil(6 / itemsPerPage) : 0;

  if (apiStatus === 'checking') {
    return (
      <div className="App">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>正在連接到 API 服務...</p>
        </div>
      </div>
    );
  }

  if (apiStatus === 'error') {
    return (
      <div className="App">
        <div className="error-message">
          <h2>服務連接錯誤</h2>
          <p>{error}</p>
          <p>請確認後端服務是否正常運行</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>財報分析 GPT</h1>
        <div className="upload-container">
          <label className="upload-button" htmlFor="file-input">
            {file ? '重新選擇檔案' : '選擇財報 PDF'}
            <input
              id="file-input"
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              style={{ display: 'none' }}
            />
          </label>
          {file && <p className="file-name">已選擇：{file.name}</p>}
        </div>
      </header>

      <main className="App-main">
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}

        {loading && (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>正在分析財報，請稍候...</p>
          </div>
        )}

        {analysis && !loading && (
          <>
            <div className="analysis-section">
              <div id="analysis-content">
                {getPageContent().map((section, index) => (
                  <div key={index} className="page">
                    <h2>{section.title}</h2>
                    <div className="analysis-content">
                      {section.content.split('\n').map((paragraph, i) => (
                        <p key={i}>{paragraph}</p>
                      ))}
                    </div>
                  </div>
                ))}
              </div>

              <div className="page-navigation">
                <button
                  className="page-button"
                  onClick={() => setCurrentPage(prev => prev - 1)}
                  disabled={currentPage === 1}
                >
                  上一頁
                </button>
                <span>第 {currentPage} 頁，共 {totalPages} 頁</span>
                <button
                  className="page-button"
                  onClick={() => setCurrentPage(prev => prev + 1)}
                  disabled={currentPage === totalPages}
                >
                  下一頁
                </button>
              </div>

              <div className="download-section">
                <button className="download-button" onClick={handleDownloadPDF}>
                  下載 PDF 報告
                </button>
              </div>
            </div>
          </>
        )}
      </main>
    </div>
  );
}

export default App;
