import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';

// 註冊 Chart.js 組件
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const FinancialCharts = ({ indicators, analysis }) => {
  // 設置圖表選項
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        font: {
          size: 16,
          family: "'Microsoft JhengHei', sans-serif",
        },
      },
    },
  };

  // 獲利能力圖表數據
  const profitabilityData = {
    labels: ['毛利率', '營業利益率', '淨利率', 'ROE', 'ROA'],
    datasets: [
      {
        label: '百分比 (%)',
        data: [
          indicators?.grossProfitMargin * 100 || 0,
          indicators?.operatingProfitMargin * 100 || 0,
          indicators?.netProfitMargin * 100 || 0,
          indicators?.ROE * 100 || 0,
          indicators?.ROA * 100 || 0,
        ],
        backgroundColor: [
          'rgba(255, 99, 132, 0.5)',
          'rgba(54, 162, 235, 0.5)',
          'rgba(255, 206, 86, 0.5)',
          'rgba(75, 192, 192, 0.5)',
          'rgba(153, 102, 255, 0.5)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  // 財務結構圖表數據
  const financialStructureData = {
    labels: ['資產', '負債', '權益'],
    datasets: [
      {
        data: [
          indicators?.totalAssets || 100,
          indicators?.totalLiabilities || 40,
          indicators?.totalEquity || 60,
        ],
        backgroundColor: [
          'rgba(54, 162, 235, 0.5)',
          'rgba(255, 99, 132, 0.5)',
          'rgba(75, 192, 192, 0.5)',
        ],
        borderColor: [
          'rgba(54, 162, 235, 1)',
          'rgba(255, 99, 132, 1)',
          'rgba(75, 192, 192, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  // 營運效率趨勢圖表數據
  const operationalEfficiencyData = {
    labels: ['第一季', '第二季', '第三季', '第四季'],
    datasets: [
      {
        label: '營業收入',
        data: [
          indicators?.quarterlyRevenue?.[0] || 100,
          indicators?.quarterlyRevenue?.[1] || 120,
          indicators?.quarterlyRevenue?.[2] || 110,
          indicators?.quarterlyRevenue?.[3] || 130,
        ],
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="charts-container">
      <div className="chart-section">
        <h3>獲利能力分析</h3>
        <Bar
          options={{
            ...options,
            plugins: {
              ...options.plugins,
              title: {
                ...options.plugins.title,
                text: '各項獲利指標比較',
              },
            },
          }}
          data={profitabilityData}
        />
      </div>

      <div className="chart-section">
        <h3>財務結構分析</h3>
        <Pie
          options={{
            ...options,
            plugins: {
              ...options.plugins,
              title: {
                ...options.plugins.title,
                text: '資產負債結構',
              },
            },
          }}
          data={financialStructureData}
        />
      </div>

      <div className="chart-section">
        <h3>營運效率趨勢</h3>
        <Line
          options={{
            ...options,
            plugins: {
              ...options.plugins,
              title: {
                ...options.plugins.title,
                text: '季度營收趨勢',
              },
            },
          }}
          data={operationalEfficiencyData}
        />
      </div>
    </div>
  );
};

export default FinancialCharts;
