import React, { useState } from 'react';
import html2pdf from 'html2pdf.js/dist/html2pdf.bundle.min';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 3; // 每頁顯示的分析區段數

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError(null);
  };

  const handleAnalyze = async () => {
    if (!file) {
      setError('請選擇一個檔案');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('分析失敗');
      }

      const data = await response.json();
      setResult(data);
    } catch (err) {
      setError('分析過程發生錯誤');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = () => {
    const element = document.getElementById('report-container');
    const opt = {
      margin: 1,
      filename: '財報分析報告.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(element).save();
  };

  // 將分析結果分成不同區段
  const getPageSections = () => {
    if (!result) return [];
    
    const sections = [
      { title: '公司概況', content: result.summary },
      { title: '獲利能力分析', content: result.profitability_analysis },
      { title: '財務結構分析', content: result.financial_structure_analysis },
      { title: '營運效率分析', content: result.operational_efficiency_analysis },
      { title: '現金流量分析', content: result.cash_flow_analysis },
      { title: '風險評估', content: result.risk_assessment },
      { title: '投資建議', content: result.investment_advice }
    ].filter(section => section.content); // 只顯示有內容的區段

    // 計算總頁數
    const totalPages = Math.ceil(sections.length / itemsPerPage);
    
    // 獲取當前頁的區段
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return {
      sections: sections.slice(startIndex, endIndex),
      totalPages
    };
  };

  const { sections, totalPages } = result ? getPageSections() : { sections: [], totalPages: 0 };

  return (
    <div className="App">
      <header className="header">
        <h1>財報分析系統</h1>
      </header>

      <div className="upload-section">
        <input
          type="file"
          onChange={handleFileChange}
          accept=".pdf"
          className="file-input"
        />
        <button
          onClick={handleAnalyze}
          disabled={loading || !file}
          className="analyze-button"
        >
          {loading ? '分析中...' : '開始分析'}
        </button>
      </div>

      {loading && (
        <div className="loading">
          <div className="loading-spinner"></div>
          <p>正在分析中，請稍候...</p>
        </div>
      )}

      {error && <div className="error-message">{error}</div>}

      {result && (
        <div id="report-container" className="report-container">
          {sections.map((section, index) => (
            <div key={index} className="report-section">
              <h2>{section.title}</h2>
              {section.title === '公司概況' && (
                <div className="financial-metrics">
                  {result.key_metrics && Object.entries(result.key_metrics).map(([key, value]) => (
                    <div key={key} className="metric-card">
                      <h4>{key}</h4>
                      <div className="metric-value">{value}</div>
                    </div>
                  ))}
                </div>
              )}
              <div dangerouslySetInnerHTML={{ __html: section.content.replace(/\n/g, '<br/>') }} />
            </div>
          ))}
          
          <div className="pagination">
            <button
              onClick={() => setCurrentPage(prev => prev - 1)}
              disabled={currentPage === 1}
            >
              上一頁
            </button>
            <span className="page-indicator">第 {currentPage} 頁，共 {totalPages} 頁</span>
            <button
              onClick={() => setCurrentPage(prev => prev + 1)}
              disabled={currentPage === totalPages}
            >
              下一頁
            </button>
          </div>

          <button onClick={handleDownloadPDF} className="download-button">
            下載 PDF 報告
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
